                try:
                    print(f"ðŸ”Š OpenAI: {text}")
                except UnicodeEncodeError:
                    print(f"[AUDIO] OpenAI: {text}")
                return True, "OpenAI success"
        except Exception as e:
            print(f"OpenAI TTS failed: {e}", file=sys.stderr)

    # Fall back to system TTS
    try:
